mapper.select:
SELECT MESSAGE_NO as messageNo, RECEIVER, SENDER, MESSAGE_CONTENT as messageContent, MESSAGE_TITLE as messageTitle, READ_CHECK as readCheck, MESSAGE_DATE as messageDate FROM MESSAGE
where sender = #{sender}
where receiver = #{receiver}
ORDER BY read_check,MESSAGE_NO DESC

mapper.update:
update message
set read_check = 1
where message_no = #{messageNo}

mapper.select:
select count(*)
from message
where receiver = #{memberId}
